// src/components/MediumPosts.js
import React from 'react';
import './MediumPosts.css';

export const MediumPosts = () => {
  const posts = [
    {
      title: "Clasificador de Hongos",
      url: "https://medium.com/@karenaraqueo/clasificador-de-hongos-e75a1b538605",
      imageUrl: "https://miro.medium.com/v2/resize:fit:1400/format:webp/1*xtYFSEdLxvXl9NzzEZ5x_w.png",
      description: "‚ÄúTodos los hongos son comestibles‚Ä¶. Al menos una vez‚Äù"
    },
    {
      title: "An√°lisis exploratorio de un dataset de precios de propiedades",
      url: "https://medium.com/@karenaraqueo/an%C3%A1lisis-exploratorio-de-un-dataset-de-precios-de-propiedades-c46639a0ed1c",
      imageUrl: "https://miro.medium.com/v2/resize:fit:1400/format:webp/1*oMAjiiHHmO-RF-x23bmwpg.png",
      description: "An√°lisis exploratorio de un dataset de precios de propiedades"
    },
    {
      title: "Distribuci√≥n Chi-Cuadrado con Perros",
      url: "https://medium.com/@karenaraqueo/distribuci%C3%B3n-chi-cuadrado-con-perros-cc1b250913f0",
      imageUrl: "https://miro.medium.com/v2/resize:fit:1400/format:webp/1*nRxHZRFLfKIYcmsn6xTF3A.png",
      description: "Distribuci√≥n Chi-Cuadrado con Perros"
    },
    {
      title: "Predicci√≥n de la Victoria en Dota 2 basada en Eventos de los Primeros 15 Minutos",
      url: "https://medium.com/@karenaraqueo/predicci%C3%B3n-de-la-victoria-en-dota-2-basada-en-eventos-de-los-primeros-15-minutos-2a66256fcb6a",
      imageUrl: "https://miro.medium.com/v2/resize:fit:1400/format:webp/1*lI4rXO_zBWv3tlaYG92YxA.png",
      description: "Predicci√≥n de la Victoria en Dota 2 basada en Eventos de los Primeros 15 Minutos"
    },
    {
      title: "Modelo Tasador de Propiedades",
      url: "https://medium.com/@karenaraqueo/modelo-tasador-de-propiedades-f6100fc10c4b",
      imageUrl: "https://miro.medium.com/v2/resize:fit:1400/format:webp/1*lNFiobwk0ajAwsulc07qUw.png",
      description: "Modelo Tasador de Propiedades"
    },
    {
      title: "Envio del Pron√≥stico del Tiempo Diario a tu Celular üì±‚òÄÔ∏è",
      url: "https://medium.com/@karenaraqueo/envio-del-pron%C3%B3stico-del-tiempo-diario-a-tu-celular-%EF%B8%8F-a6a2a565874c",
      imageUrl: "https://miro.medium.com/v2/resize:fit:1400/format:webp/1*-oJ2uMpN8xcEkTmMwFNDdw.png",
      description: "Envio del Pron√≥stico del Tiempo Diario a tu Celular üì±‚òÄÔ∏è"
    }
  ];

  return (
    <section className="medium-posts" id="medium-posts">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="medium-posts-bx wow zoomIn">
              <h2>Mis Publicaciones en Medium</h2>
              <div className="row">
                {posts.map((post, index) => (
                  <div className="col-md-4" key={index}>
                    <div className="post-card">
                      <a href={post.url} target="_blank" rel="noopener noreferrer">
                        <img src={post.imageUrl} alt={post.title} className="post-image" />
                        <div className="post-content">
                          <h3>{post.title}</h3>
                          <p>{post.description}</p>
                        </div>
                      </a>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
