// src/components/MediumPosts.js
import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

export const MediumPosts = () => {
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 3
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 2
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  const posts = [
    {
      title: "Envio del Pron√≥stico del Tiempo Diario a tu Celular üì±‚òÄÔ∏è",
      url: "https://medium.com/@Karenaraqueo/envio-del-pron√≥stico-del-tiempo-diario-a-tu-celular-üì±‚òÄÔ∏è-e7d0f77d1",
      readTime: "7 min read",
      date: "May 19, 2024"
    },
    {
      title: "Modelo Tasador de Propiedades",
      url: "https://medium.com/@Karenaraqueo/modelo-tasador-de-propiedades-ef5b83b4d",
      readTime: "3 min read",
      date: "Apr 16, 2024"
    },
    {
      title: "Predicci√≥n de la Victoria en Dota 2 basada en Eventos de los Primeros 15 Minutos",
      url: "https://medium.com/@Karenaraqueo/predicci√≥n-de-la-victoria-en-dota-2-basada-en-eventos-de-los-primeros-15-minutos-56ad89e",
      readTime: "3 min read",
      date: "Apr 16, 2024"
    },
    {
      title: "Clasificador de Hongos",
      url: "https://medium.com/@Karenaraqueo/clasificador-de-hongos-34d0fdf",
      readTime: "3 min read",
      date: "Apr 16, 2024"
    },
    {
      title: "An√°lisis exploratorio de un dataset de precios de propiedades",
      url: "https://medium.com/@Karenaraqueo/an√°lisis-exploratorio-de-un-dataset-de-precios-de-propiedades-34fd08",
      readTime: "5 min read",
      date: "Apr 16, 2024"
    },
    {
      title: "Distribuci√≥n Chi-Cuadrado con Perros",
      url: "https://medium.com/@Karenaraqueo/distribuci√≥n-chi-cuadrado-con-perros-e0d9f08",
      readTime: "5 min read",
      date: "Apr 4, 2024"
    }
  ];

  return (
    <section className="medium-posts" id="medium-posts">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="medium-posts-bx wow zoomIn">
              <h2>Mis Publicaciones en Medium</h2>
              <Carousel responsive={responsive} infinite={true} className="owl-carousel owl-theme medium-posts-slider">
                {posts.map((post, index) => (
                  <div className="item" key={index}>
                    <a href={post.url} target="_blank" rel="noopener noreferrer">
                      <div className="post-content">
                        <h3>{post.title}</h3>
                        <p>{post.readTime} ¬∑ {post.date}</p>
                      </div>
                    </a>
                  </div>
                ))}
              </Carousel>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
